import { Miew, MiewOptions } from 'miew';
import { MiewTheme } from './theming';
declare type DeepPartial<T> = {
    [P in keyof T]?: DeepPartial<T[P]>;
};
declare type ViewerProps = {
    onInit?: (miew: Miew) => void;
    options?: MiewOptions;
    theme?: DeepPartial<MiewTheme>;
};
declare const Viewer: ({ onInit, options, theme }: ViewerProps) => import("@emotion/react/jsx-runtime").JSX.Element;
export { Viewer };
